import { loadPages } from '../actions/page'

class Index extends React.Component {
  state = {
    pages: []
  }

  onReloadPages = () => {
    loadPages()
  }

  componentDidMount() {
    this.onReloadPages()
  }

  render() {
    return (
      // <Index
      //   pages={this.props.pages}
      //   onReloadPages={this.onReloadPages} />
      <div style={{ textAlign: 'center', fontSize: '25px' }}>
        <p>Welcome to <span style={{ color: '#d35d3f' }}>Koyo</span> Utility Application</p>
        <p>Please select desired function from the navigation menu.</p>
      </div>
    )
  }
}

export default Index
