class Index extends React.Component {
  state = {
    pages: []
  }

  onReloadPages = () => {
    loadPages()
  }

  componentDidMount() {
    this.onReloadPages()
  }

  render() {
    return (
      <div style={{ textAlign: 'center', fontSize: '25px' }}>
        <p>Welcome to <span style={{ color: '#d35d3f' }}>Koyo</span> Utility Application</p>
        <p>Please select desired function from the navigation menu.</p>
      </div>
    )
  }
}

export default Index
