import { loadPages } from '../actions/page'
import  Pages from '../components/page'
import { connect } from 'react-redux'

class Index extends React.Component {
  state = {
    pages: []
  }

  onReloadPages = () => {
    loadPages()
  }

  componentDidMount() {
    this.onReloadPages()
  }

  render() {
    return (
      <Pages
        pages={this.props.pages}
        onReloadPages={this.onReloadPages} />
